import React from 'react';
import './Portfolio.css';

const projects = [
  {
    title: 'GTV Vietnam – Corporate & Retail Digital Banking',
    description:
      'Led product strategy and AML integration across T24 & Backbase platforms for both corporate and retail banking users.',
    image: 'https://via.placeholder.com/400x200?text=GTV+Vietnam'
  },
  {
    title: 'Axon Active – Mortgage Lending Platform (Migros Bank)',
    description:
      'Defined end-to-end digital mortgage lending workflows including ZEK integration, affordability simulation, and document handling.',
    image: 'https://via.placeholder.com/400x200?text=Axon+Active'
  },
  {
    title: 'BIC Bank Cambodia – Retail Digital Channel Revamp',
    description:
      'Delivered a modern AML screening solution, centralized payment hub, and KPI dashboard for digital retail banking operations.',
    image: 'https://via.placeholder.com/400x200?text=BIC+Bank+Cambodia'
  },
  {
    title: 'Home Credit & FeCredit – Consumer Finance Solutions',
    description:
      'Worked on loan origination systems, collections strategies, and decisioning platform enhancements using PEGA and Loxon.',
    image: 'https://via.placeholder.com/400x200?text=Home+Credit+FeCredit'
  },
  {
    title: 'ABBank – Core Banking & Backbase UX Redesign',
    description:
      'Integrated payment gateway with Backbase, optimized onboarding processes, and improved customer lifecycle UX flows.',
    image: 'https://via.placeholder.com/400x200?text=ABBank'
  },
  {
    title: 'Futa Bus CRM Ticket Selling – Futa Express System',
    description: 'CRM for ticket sales and operation for Futa Bus Line.',
    image: 'https://via.placeholder.com/400x200?text=Futa+Bus'
  },
  {
    title: 'Prozy – Real Estate CRM Product',
    description: 'Customer relationship management solution tailored for real estate business needs.',
    image: 'https://via.placeholder.com/400x200?text=Prozy+CRM'
  },
  {
    title: 'Maersk Line – Logistics Service Enhancements',
    description: 'Networking management and Salesforce CRM enhancement for global logistics.',
    image: 'https://via.placeholder.com/400x200?text=Maersk+Logistics'
  },
  {
    title: 'Home Credit – Collection CRM, MUCH, Loxon, Astah Modeling',
    description: 'Developed CRM for collections, mobile unify hub, and visual modeling tools integration.',
    image: 'https://via.placeholder.com/400x200?text=HomeCredit+CRM'
  },
  {
    title: 'JACCS – Credit Card System Integration',
    description: 'Integrated VISA and MasterCard systems with JACCS credit card management.',
    image: 'https://via.placeholder.com/400x200?text=JACCS+Visa+Master'
  },
  {
    title: 'Amaris Consulting – FECredit, Manulife, Prudential',
    description: 'Led PEGA LOS for FECredit, CRM Web/Mobile for Manulife, and back office for Prudential.',
    image: 'https://via.placeholder.com/400x200?text=Amaris+Consulting'
  },
  {
    title: 'FPT Software – GE, Canon Projects',
    description: 'System analysis for GE; CRM and virtual meta class systems for Canon.',
    image: 'https://via.placeholder.com/400x200?text=FPT+GE+Canon'
  },
  {
    title: 'Xebia – ABBank',
    description: 'Banking transformation using Backbase and T24 systems.',
    image: 'https://via.placeholder.com/400x200?text=Xebia+ABBank'
  }
];

const Portfolio: React.FC = () => {
  return (
    <section className="portfolio">
      <h2 className="section-title">My Projects</h2>
      <div className="project-list">
        {projects.map((project, index) => (
          <div className="project-card" key={index}>
            <img src={project.image} alt={project.title} className="project-image" />
            <div className="project-info">
              <h3 className="project-title">{project.title}</h3>
              <p className="project-description">{project.description}</p>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Portfolio;
