import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import classNames from 'classnames';
import './Sidebar.css';

interface MenuItem {
  name: string;
  path: string;
  icon: string;
}

const menuItems: MenuItem[] = [
  { name: 'Home', path: '/', icon: 'fa-home' },
  { name: 'About Me', path: '/about', icon: 'fa-user' },
  { name: 'Resume', path: '/resume', icon: 'fa-file-alt' },
  { name: 'Portfolio', path: '/portfolio', icon: 'fa-briefcase' },
  { name: 'Blog', path: '/blog', icon: 'fa-blog' },
  { name: 'Contact', path: '/contact', icon: 'fa-envelope' },
];

const Sidebar: React.FC = () => {
  const location = useLocation();

  const socialLinks = [
    { platform: 'facebook-f', url: 'https://www.facebook.com/taanh.luan.5' },
    { platform: 'instagram', url: 'https://www.instagram.com/taanhluan/' },
    { platform: 'github', url: 'https://github.com/taanhluan' },
    { platform: 'linkedin-in', url: 'https://www.linkedin.com/in/taanhluan/' },
  ];

  return (
    <div className="sidebar">
      <div className="avatar-box">
        <img
          src="https://i.pravatar.cc/100"
          alt="Jonathan Ta avatar"
          className="avatar"
        />
        <h2>Jonathan Ta</h2>
        <div className="social-icons">
          {socialLinks.map((item) => (
            <a
              key={item.platform}
              href={item.url}
              target="_blank"
              rel="noopener noreferrer"
              aria-label={item.platform}
            >
              <i className={`fab fa-${item.platform}`}></i>
            </a>
          ))}
        </div>
      </div>

      <ul className="nav-menu">
        {menuItems.map((item) => (
          <li
            key={item.path}
            className={classNames({ active: location.pathname === item.path })}
          >
            <Link to={item.path}>
              <i className={`fas ${item.icon}`}></i>
              <span>{item.name}</span>
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Sidebar;
